JSLogger = function() {
  this.getLogger= function(config)  {
     return {
       warn : function(text) {
         console.log("jslogger:",text);
       },
       error : function(text) {
         console.log("jslogger:",text);
       },
       trace : function(text) {
         console.log("jslogger:",text);
       },
       debug : function(text) {
         console.log("jslogger:",text);
       }      
     };
  };
};

module.exports = {
   'logger' : new JSLogger().getLogger()
};
