JSLogger = function() {
  this.getLogger= function(config)  {
     return {
       warn : function(text) {
	     console.log(" ");
         console.log("jslogger-warn:",text);
       },
       error : function(text) {
	     console.log(" ");
         console.log("jslogger-error:",text);
       },
       trace : function(text) {
	     console.log(" ");
         console.log("jslogger-trace:",text);
       },
       debug : function(text) {
	     console.log(" ");
         console.log("jslogger-debug:",text);
       }
     };
  };
};

module.exports = {
   'logger' : new JSLogger().getLogger()
};
